// Get the top array from your JSON
const topItems = $('Trends').first().json.related_queries.top;

// Filter the items to only include those with extracted_value > 30
const filtered = topItems.filter(item => item.extracted_value > 30);

// Map the filtered items to their query values and join them with commas
const resultString = filtered.map(item => item.query).join(', ');

// Return the result in a new JSON property
return [{ json: { result: resultString } }];

